    public class NamesGenerator
    {
        Markov.MarkovChain<char> asianChain = new MarkovChain<char>(2);
        Markov.MarkovChain<char> russianChain = new MarkovChain<char>(2);
        Markov.MarkovChain<char> englishChain = new MarkovChain<char>(2);
        Markov.MarkovChain<char> countryChain = new MarkovChain<char>(2);

        public string GetAsianTownName(Random random)
        {
            return new string(asianChain.Chain(random).ToArray());
        }

        public string GetEnglishTownName(Random random)
        {
            return new string(englishChain.Chain(random).ToArray());
        }

        public string GetRussianTownName(Random random)
        {
            return new string(russianChain.Chain(random).ToArray());
        }

        public string GetCountryName(Random random)
        {
            return new string(countryChain.Chain(random).ToArray());
        }

